    /**
     * 随机一个名字
     * @return
     * @throws Throwable
     */
    public Object randomName() throws Throwable {

    	int roleId = this.roleId();

        Role role = this.roleDao.findOne(roleId);
        int serverId = role.getServerId();

        String[] firstName = FirstName;
        String[] lastName = LastName;

        while (true) {

            Random random = new Random();
            int r1 = random.nextInt(firstName.length);
            int r2 = random.nextInt(lastName.length);
            
            String name = firstName[r1] + lastName[r2];
            
            int n = this.roleDao.countByName(name, serverId);

            if (n > 0) {
                continue;
            }

            return this.success(name);
        }
    }

    /**
     * 
     * @param serverId
     * @param s
     * @return
     * @throws Exception 
     */
    public Object getPlayer(int serverId, String s) throws Exception {

    	int userId = this.userId();
    	if (userId == 0) {
    		return this.returnError(-1,  "Session disconnect");
    	}

    	Token token = this.tokenDao.findOne(this.tokenDao.getTokenByUserId(userId));

		Server server = null;

		List<Server> servers = serverDao.findAll();
		for (Server serverTable : servers) {

			if (serverTable.getId() == serverId) {
				server = serverTable;
			}
		}

		if (server == null) {
			return this.returnError(this.lineNum(), "没有这个服");
		}

        Result result = new Result();

        int roleId = 0;
        
        Role role = this.roleDao.getByServerId(userId, serverId);
        if (role == null) {

        	role = this.roleDao.create(userId, serverId);

        	this.storyDao.create(role.getRoleId(), 1, BaseStory.COPY_TYPE);
        	this.storyDao.create(role.getRoleId(), 1, BaseStory.HERO_COPY_TYPE);

            int[] list = new int[]{10002};
            for (int heroId : list) {
            	this.heroDao.create(role, heroId, result);
            }

            roleId = role.getRoleId();

        } else {

        	roleId = role.getRoleId();

            List<Hero> heroList = this.heroDao.findAll(roleId);

            // 新手引导未完成  数据重置
            if (role.getProgress() < 49) {
                if (heroList.size()  > 1) {

                	DateNumModel dateNumModel = this.dateNumDao.findOne(roleId);
                	dateNumModel.setBarCoinNum(0);
                	dateNumModel.setBarTime(0l);
                	this.dateNumDao.save(roleId, dateNumModel);

                	role.setBarGoldTime(0);

                	// this.roleDao.update(role, result);

                    List<Hero> heros = new ArrayList<Hero>();

                    for (Hero hero : heroList) {
                        if (hero.getHeroId() != 10002) {
                        	this.heroDao.delete(hero);
                        } else {
                        	heros.add(hero);
                        }
                    }

                    heroList = heros;
                }
            }

            for (Hero hero : heroList) {
                result.addHero(hero);
            }
        }

        this.getHandler().roleId = roleId;
        this.getHandler().serverId = serverId;

        EchoHandler.close(this.getHandler().getSession(), roleId);

        token.setRoleId(roleId);
        token.setServerId(serverId);

        this.tokenDao.setToken(token);

        result.setAp(role.ap(), role.apCoolTime());
        result.setMoney(role.getCoin(), role.getGold());
        result.setTeam(role.getExp());

        List<Item> itemList = this.itemDao.findAll(roleId);
        if (itemList.size() == 0) {
        	result.setValue("item", new Object[]{});
        } else {
            for (Item itemTable : itemList) {
                result.addItem(itemTable);
            }
        }

        List<Story> storyList = this.storyDao.findAll(roleId);

        Story lastCopy = null;
        Story lastHeroCopy = null;
        
        int storyNum = 0;

        for (Story story : storyList) {

            if (story.getType() == BaseStory.COPY_TYPE) {
            	storyNum++;
            	lastCopy = story;
            } else if (story.getType() == BaseStory.HERO_COPY_TYPE) {
            	lastHeroCopy = story;
            }
            result.addCopy(story);
        }

        if (lastCopy.getStar() > 0) {
            BaseStory baseStory = this.baseStoryDao.findOne(lastCopy.getStoryId() + 1, lastCopy.getType());
            if (baseStory != null) {
            	result.addCopy(this.storyDao.create(roleId, baseStory.getId(), baseStory.getType()));
            }
        }

        if (lastHeroCopy.getStar() > 0) {
            BaseStory baseStory = this.baseStoryDao.findOne(lastHeroCopy.getStoryId() + 1, lastHeroCopy.getType());
            if (baseStory != null) {
            	result.addCopy(this.storyDao.create(roleId, baseStory.getId(), baseStory.getType()));
            }
        }

        DateNumModel dateNumModel = this.dateNumDao.findOne(roleId);
        
        // 点金手   当前次数 , 最大次数 , 需要的元宝  最少获得的金币
        result.setValue("get_coin", new int[]{dateNumModel.getBuyCoinNum(), role.maxGetCoinTimes(), MissionService.getCoinNeedGold(dateNumModel.getBuyCoinNum()), MissionService.getMinCoin(role.level(), dateNumModel.getBuyCoinNum())});
        result.setValue("buy_mp", new int[]{dateNumModel.getBuyApNum(), role.getVip() + 1, dateNumModel.buyApNeedGold()});

        // 魂石商店
        List<BaseHeroShop> soulList = baseHeroShopDao.findAll("soul");

        ArrayList<Integer> soulIds = new ArrayList<Integer>();
        int j = 0;
        for (BaseHeroShop item : soulList) {
            soulIds.add(item.getItemId());
            j++;
            if (j >= 4) {
                break;
            }
        }

        long time = System.currentTimeMillis();
        long bar_gold_time = (long)role.barGoldTime * 1000l - time;

        result.setValue("souls", soulIds);
        // 普通招募次数
        result.setValue("bar_coin_times", dateNumModel.barCoinNum);
        // 英雄招募冷却时间
        result.setValue("bar_gold_time", bar_gold_time > 0 ? bar_gold_time : 0);
        // 普通招募冷却时间
        result.setValue("bar_coin_time", dateNumModel.barTime > time ? dateNumModel.barTime - time : 0);

        result.setValue("vip", role.vip());

        // 重置关卡进度
        if (role.progress >= 1008) {
        	if (storyNum < 3) {
        		role.progress = 1002;
        	}
        }

        result.setValue("progress", role.getProgress());

        result.setValue("setstring", role.getStr());

        result.setValue("name", role.getName());
        result.setValue("role_id", role.getRoleId());
        result.setValue("avatar", role.getAvatar());
        result.setValue("gender", role.getAvatar());

        OnlineService onlineService = (OnlineService) SpringContextUtils.getBean(OnlineService.class);
        int online_num = onlineService.getNum(role);

        result.setValue("online_num", online_num);
        result.setValue("online_coin", OnlineService.coin(online_num));
        result.setValue("online_gold", OnlineService.gold(online_num));

        // 竞技场排名第一的那个
        int No1Id = this.arenaDao.getList(serverId).get(0);

        if (this.roleDao.isPlayer(No1Id)) {
        	result.setValue("No1name", this.roleDao.findOne(No1Id).name);
        	
        	List<Hero> heros = this.heroDao.findAll(No1Id);
        	
        	int hId = 0;
        	for (Hero hero : heros) {
				if (hero.getIsBattle() && hero.getPosition() == 1) {
					hId = hero.getHeroId();
				}
			}
        	result.setValue("No1hero", hId);

        } else {
        	ArenaPlayerModel p = new ArenaPlayerModel();
        	p.roleId = No1Id;
        	this.arenaDao.toPlayer(p, serverId);
            result.setValue("No1name", p.name);
            
            Object o = p.generalList.get(0);
            Object[] oo = (Object[])o;

            result.setValue("No1hero", (int)oo[0]);
        }

        return this.success(result.toMap());
    }

    /**
     * 设置名字
     * @param name
     * @throws Exception
     */
    public Object setName(String name) throws Exception {

        int nameLength = name.toCharArray().length;
        if (nameLength > 8 || nameLength <= 0) {
            return this.returnError(this.lineNum(), "名字长度不符合,最多8个汉字");
        }

        if (name.indexOf(" ") >= 0) {
            return this.returnError(this.lineNum(), "名字不能有空格");
        }

        if (name.indexOf("	") >= 0) {
            return this.returnError(this.lineNum(), "名字不能有制符表");
        }

        if (name.indexOf("\n") >= 0) {
            return this.returnError(this.lineNum(), "名字不能有回车");
        }

        for (String string : sensitives) {
            if (name.indexOf(string) >= 0) {
                return this.returnError(this.lineNum(), "包含敏感词请重新起名你懂的~");
            }
        }

        Role role = this.roleDao.findOne(this.roleId());

        int count = this.roleDao.countByName(name, role.getServerId());
        if (count > 0) {
        	return this.returnError(this.lineNum(), "此名字已被使用");
        }

        Result result = new Result();
        if (!role.getName().equals("") && !name.equals(role.getName())) {

        	int gold = 100;

            if (role.getGold() < gold) {
            	return this.returnError(2, ErrorResult.NotEnoughGold);
            } else {
            	this.roleDao.subGold(role, gold, "修改名字->" + name , FinanceLog.STATUS_UPDATE_NAME, result);
            }
        }

        role.setName(name);

        this.roleDao.update(role, result);

        return this.success(result.toMap());
    }

