    /**
     * 随机一个名字
     * @return
     * @throws Throwable
     */
    public String randomName() throws Throwable {

    	int roleId = this.roleId();

        Role role = roleDao.findOne(roleId);
        int serverId = role.getServerId();

        String[] firstName = FirstName;
        String[] lastName = LastName;

        while (true) {

            Random random = new Random();
            int r1 = random.nextInt(firstName.length);
            int r2 = random.nextInt(lastName.length);
            
            String name = firstName[r1] + lastName[r2];
            
            int n = roleDao.countByName(name, serverId);

            if (n > 0) {
                continue;
            }

            return name;
        }
    }

